          <h3 className="text-3xl font-sans font-extrabold text-slate-900 tracking-tight">{value}</h3>
          {sub && <p className="text-xs font-medium text-slate-400 mt-2">{sub}</p>}
        </div>
      </CardContent>
    </Card>
  );
}

